template.template_md:
{% block content %}
<style>
  @page { margin: 0.5in; size: letter landscape; }

  /* Filter dropdowns inside headers */
  .report-table thead th select{
    width:100%; margin-top:5px; padding:4px; border-radius:4px; border:1px solid #ccc; font-weight:normal;
    background:#fff;
  }

  /* Date column width */
  .report-table th:nth-child(5), .report-table td:nth-child(5){ width:150px; }

  /* Header label row + indicators */
  .th-topline{ display:flex; align-items:center; gap:.4rem; }
  .th-label{ position:relative; }
  /* indicator beside the name via CSS, driven by classes on TH */
  th.sorted.asc  .th-label::after{ content:"▲"; margin-left:.25rem; color:#666; font-size:.9em; }
  th.sorted.desc .th-label::after{ content:"▼"; margin-left:.25rem; color:#666; font-size:.9em; }
</style>

<div class="report-header">
  <div class="header-logo">
    <img src="https://github.com/amidaware/reporting-templates/blob/master/assets/gbtnavy%20(256).png?raw=true" alt="Amidaware Logo" style="width:80px;height:auto;">
  </div>
  <div class="header-titles">
    <h1>TRMM Agents Installation Report</h1>
    <h2 id="report-subtitle">Client: All | Site: All</h2>
  </div>
  <div class="header-date">
    <h2>Report Date:<br>{{ report_run_timestamp.strftime('%B %d, %Y') }}</h2>
  </div>
</div>

{% set clients = [] %}
{% set sites = [] %}
{% for item in data_sources.agents %}
  {% if item.site__client__name not in clients %}{% set _ = clients.append(item.site__client__name) %}{% endif %}
  {% if item.site__name not in sites %}{% set _ = sites.append(item.site__name) %}{% endif %}
{% endfor %}

<div class="section">
  <table id="reportTable" class="report-table">
    <thead>
      <tr>
        <th data-col="0">
          <div class="th-topline">
            <span class="th-label">Client</span>
          </div>
          <select id="client-filter">
            <option value="all">All Clients</option>
            {% for client in clients | sort %}
            <option value="{{ client }}">{{ client }}</option>
            {% endfor %}
          </select>
        </th>
        <th data-col="1">
          <div class="th-topline">
            <span class="th-label">Site</span>
          </div>
          <select id="site-filter">
            <option value="all">All Sites</option>
            {% for site in sites | sort %}
            <option value="{{ site }}">{{ site }}</option>
            {% endfor %}
          </select>
        </th>
        <th data-col="2">
          <div class="th-topline"><span class="th-label">Agent</span></div>
        </th>
        <th data-col="3">
          <div class="th-topline"><span class="th-label">User</span></div>
        </th>
        <th data-col="4">
          <div class="th-topline"><span class="th-label">Installation Date</span></div>
        </th>
      </tr>
    </thead>
    <tbody id="report-table-body">
      {% for item in data_sources.agents %}
      <tr>
        <td>{{ item.site__client__name }}</td>
        <td>{{ item.site__name }}</td>
        <td>{{ item.hostname }}</td>
        <td>{{ item.last_logged_in_user }}</td>
        <td data-sort-value="{% if item.created_time %}{{ item.created_time.isoformat() }}{% else %}N/A{% endif %}">
          {% if item.created_time %}{{ item.created_time.strftime('%Y-%m-%d %H:%M') }}{% else %}N/A{% endif %}
        </td>
      </tr>
      {% endfor %}
    </tbody>
  </table>
</div>

<script>
document.addEventListener("DOMContentLoaded", () => {
  const clientFilter = document.getElementById("client-filter");
  const siteFilter   = document.getElementById("site-filter");
  const tableBody    = document.getElementById("report-table-body");
  const reportSubtitle = document.getElementById("report-subtitle");
  const headers = Array.from(document.querySelectorAll("#reportTable thead th"));

  let currentSortColumn = null;
  let currentSortDirection = "asc";

  function updateIndicators() {
    headers.forEach(h => h.classList.remove("sorted","asc","desc"));
    if (currentSortColumn !== null) {
      const th = headers[currentSortColumn];
      th.classList.add("sorted", currentSortDirection);
      th.setAttribute("aria-sort", currentSortDirection);
    } else {
      headers.forEach(h => h.removeAttribute("aria-sort"));
    }
  }

  function applyFilters() {
    const selClient = clientFilter.value;
    const selSite   = siteFilter.value;

    reportSubtitle.textContent =
      `Client: ${selClient === 'all' ? 'All' : selClient} | Site: ${selSite === 'all' ? 'All' : selSite}`;

    const rows = Array.from(tableBody.rows);
    rows.forEach(row => {
      const client = row.cells[0].textContent.trim();
      const site   = row.cells[1].textContent.trim();
      const show = (selClient === "all" || client === selClient) &&
                   (selSite   === "all" || site   === selSite);
      row.style.display = show ? "" : "none";
    });

    if (currentSortColumn !== null) sortTable(currentSortColumn, currentSortDirection, false);
  }

  function sortTable(colIndex, direction, toggle = true) {
    const rows = Array.from(tableBody.rows);
    const visible = rows.filter(r => r.style.display !== "none");

    if (toggle) {
      if (currentSortColumn === colIndex) {
        currentSortDirection = currentSortDirection === "asc" ? "desc" : "asc";
      } else {
        currentSortDirection = "asc";
      }
    }
    currentSortColumn = colIndex;

    visible.sort((a,b) => {
      let aVal, bVal;
      if (colIndex === 4) {
        aVal = a.cells[colIndex].getAttribute("data-sort-value") || "";
        bVal = b.cells[colIndex].getAttribute("data-sort-value") || "";
        const da = new Date(aVal), db = new Date(bVal);
        if (isNaN(da) && isNaN(db)) return 0;
        if (isNaN(da)) return 1;
        if (isNaN(db)) return -1;
        return da - db;
      } else {
        aVal = a.cells[colIndex].textContent.trim();
        bVal = b.cells[colIndex].textContent.trim();
        return aVal.localeCompare(bVal, undefined, { numeric:true, sensitivity:"base" });
      }
    });

    if (currentSortDirection === "desc") visible.reverse();

    tableBody.innerHTML = "";
    visible.forEach(r => tableBody.appendChild(r));
    rows.filter(r => r.style.display === "none").forEach(r => tableBody.appendChild(r));

    updateIndicators();
  }

  // Click sort on label only; selects won’t trigger sort
  headers.forEach((th, idx) => {
    const label = th.querySelector(".th-label");
    if (label) {
      label.style.cursor = "pointer";
      label.addEventListener("click", () => sortTable(idx, currentSortDirection));
    }
  });

  clientFilter.addEventListener("change", applyFilters);
  siteFilter.addEventListener("change", applyFilters);

  // Initial render; no default sort so no indicator until user clicks
  applyFilters();
});
</script>
{% endblock %}
