template.template_md:
{% block content %}
<style>
  @page { margin: 0.5in; size: letter landscape; }

  .report-header {
    display: grid; grid-template-columns: auto 1fr auto; gap: 12px; align-items: center; margin-bottom: 10px;
  }
  .report-table { width: 100%; border-collapse: collapse; }
  .report-table th, .report-table td { border: 1px solid #ddd; padding: 6px 8px; vertical-align: top; }
  .report-table th { cursor: pointer; user-select: none; }

  /* Inline label + sort indicator */
  .th-topline { display: flex; align-items: center; gap: 6px; }
  .sort-indicator { font-size: 0.9em; color: #666; min-width: 1ch; }
  .report-table thead th select {
    width: 100%; margin-top: 5px; padding: 4px; border-radius: 4px; border: 1px solid #ccc; font-weight: normal; background: #fff;
  }

  /* Wider device column (name + last seen) — col 3 (index 2) */
  .report-table th:nth-child(3),
  .report-table td:nth-child(3) { width: 240px; }

  .row-count { font-size: 0.9em; color: #555; }
</style>

<div class="report-header">
  <div class="header-logo">
    <img src="https://github.com/amidaware/reporting-templates/blob/master/assets/gbtnavy%20(256).png?raw=true" alt="Amidaware Logo" style="width: 80px; height: auto;">
  </div>
  <div class="header-titles">
    <h1>Antivirus Report</h1>
    <h2 id="report-subtitle">Client: All | Site: All</h2>
    <div class="row-count">Filtered Row Count: <span id="filteredRowCount">0</span></div>
  </div>
  <div class="header-date">
    <h2>Report Date:<br>{{ report_run_timestamp.strftime('%B %d, %Y') }}</h2>
  </div>
</div>

{% set clients = [] %}
{% set sites = [] %}
{% for item in data_sources.agentsList %}
  {% if item.site__client__name not in clients %}
    {% set _ = clients.append(item.site__client__name) %}
  {% endif %}
  {% if item.site__name not in sites %}
    {% set _ = sites.append(item.site__name) %}
  {% endif %}
{% endfor %}

<table id="reportTable" class="report-table" aria-describedby="report-subtitle">
  <thead>
    <tr>
      <!-- Client (col 0) -->
      <th data-col="client">
        <div class="th-topline">
          <span class="th-label">Client</span>
          <span class="sort-indicator" data-for-col="0"></span>
        </div>
        <select id="clientFilterH" aria-label="Client filter">
          <option value="all">All Clients</option>
          {% for client in clients | sort %}
          <option value="{{ client }}">{{ client }}</option>
          {% endfor %}
        </select>
      </th>

      <!-- Site (col 1) -->
      <th data-col="site">
        <div class="th-topline">
          <span class="th-label">Site</span>
          <span class="sort-indicator" data-for-col="1"></span>
        </div>
        <select id="siteFilterH" aria-label="Site filter">
          <option value="all">All Sites</option>
          {% for site in sites | sort %}
          <option value="{{ site }}">{{ site }}</option>
          {% endfor %}
        </select>
      </th>

      <th data-col="device">
        <div class="th-topline">
          <span class="th-label">Device Name</span>
          <span class="sort-indicator" data-for-col="2"></span>
        </div>
      </th>

      <th data-col="user">
        <div class="th-topline">
          <span class="th-label">User</span>
          <span class="sort-indicator" data-for-col="3"></span>
        </div>
        <select id="user-filter-h" aria-label="User filter">
          <option value="all">All Users</option>
        </select>
      </th>

      <th data-col="os">
        <div class="th-topline">
          <span class="th-label">Device OS</span>
          <span class="sort-indicator" data-for-col="4"></span>
        </div>
        <select id="os-filter-h" aria-label="OS filter">
          <option value="all">All OS</option>
        </select>
      </th>

      <th data-col="av">
        <div class="th-topline">
          <span class="th-label">Antivirus</span>
          <span class="sort-indicator" data-for-col="5"></span>
        </div>
        <select id="av-filter-h" aria-label="Antivirus filter">
          <option value="all">All Antivirus</option>
        </select>
      </th>
    </tr>
  </thead>

  <tbody id="report-table-body">
    {% for item in data_sources.agentsList %}
    <tr>
      <td class="cell-client">{{ item.site__client__name }}</td>
      <td class="cell-site">{{ item.site__name }}</td>
      <td class="cell-device" data-sort-value="{{ item.hostname }}">
        <b class="device-name">{{ item.hostname }}</b><br>
        {% if item.last_seen %}
        <small class="last-seen" data-sort-value="{{ item.last_seen.isoformat() }}">
          Last Seen: {{ item.last_seen.astimezone(ZoneInfo('America/New_York')).strftime('%Y-%m-%d %H:%M:%S') }}
        </small>
        {% else %}
        <small class="last-seen" data-sort-value="N/A">&nbsp;</small>
        {% endif %}
      </td>
      <td class="cell-user">{{ item.last_logged_in_user }}</td>
      <td class="cell-os" style="word-wrap: break-word;">{{ item.operating_system }}</td>
      <td class="cell-av" style="word-wrap: break-word;">
        {% if item.custom_fields and 'Antivirus' in item.custom_fields %}
          {{ item.custom_fields.Antivirus }}
        {% endif %}
      </td>
    </tr>
    {% endfor %}
  </tbody>
</table>

<script>
(function() {
  const $  = s => document.querySelector(s);
  const $$ = s => Array.from(document.querySelectorAll(s));
  const tbody = $('#report-table-body');

  // Only header filters exist
  const selects = {
    client: $('#clientFilterH'),
    site:   $('#siteFilterH'),
    user:   $('#user-filter-h'),
    os:     $('#os-filter-h'),
    av:     $('#av-filter-h')
  };

  let currentSort = { colIndex: null, dirAsc: true };

  function getCellText(row, selector, fallbackIndex=null) {
    const el = row.querySelector(selector);
    if (el) return el.textContent.trim();
    if (fallbackIndex !== null) return row.cells[fallbackIndex].textContent.trim();
    return '';
  }

  function buildOptionsFromSet(selectEl, set, allLabel) {
    if (!selectEl) return;
    const current = selectEl.value || 'all';
    const opts = [ `<option value="all">${allLabel}</option>` ]
      .concat([...set].sort((a,b)=>String(a).localeCompare(String(b), undefined, {numeric:true, sensitivity:'base'}))
      .map(v => `<option value="${String(v)}">${String(v)}</option>`));
    selectEl.innerHTML = opts.join('');
    if ([...set].includes(current) || current === 'all') selectEl.value = current;
  }

  function updateDropdownsFromVisible() {
    const rows = $$('#reportTable tbody tr').filter(r => r.style.display !== 'none');
    const clients = new Set(), sites = new Set(), users = new Set(), oss = new Set(), avs = new Set();

    rows.forEach(r => {
      clients.add(getCellText(r, '.cell-client', 0));
      sites.add(getCellText(r, '.cell-site', 1));
      const user = getCellText(r, '.cell-user', 3); if (user) users.add(user);
      const os   = getCellText(r, '.cell-os', 4);   if (os)   oss.add(os);
      const av   = getCellText(r, '.cell-av', 5);   if (av)   avs.add(av);
    });

    buildOptionsFromSet(selects.client, clients, 'All Clients');
    buildOptionsFromSet(selects.site,   sites,   'All Sites');
    buildOptionsFromSet(selects.user,   users,   'All Users');
    buildOptionsFromSet(selects.os,     oss,     'All OS');
    buildOptionsFromSet(selects.av,     avs,     'All Antivirus');
  }

  function setSortIndicator(colIndex, dirAscOrNull) {
    // Clear all
    $$('.sort-indicator').forEach(s => s.textContent = '');
    if (dirAscOrNull === null) return;
    const el = $(`.sort-indicator[data-for-col="${colIndex}"]`);
    if (el) el.textContent = dirAscOrNull ? '▲' : '▼';
  }

  function filterTable() {
    const clientVal = selects.client?.value || 'all';
    const siteVal   = selects.site?.value   || 'all';
    const userVal   = selects.user?.value   || 'all';
    const osVal     = selects.os?.value     || 'all';
    const avVal     = selects.av?.value     || 'all';

    let visible = 0;

    $$('#reportTable tbody tr').forEach(row => {
      const client = getCellText(row, '.cell-client', 0);
      const site   = getCellText(row, '.cell-site', 1);
      const user   = getCellText(row, '.cell-user', 3);
      const os     = getCellText(row, '.cell-os', 4);
      const av     = getCellText(row, '.cell-av', 5);

      const show = (clientVal === 'all' || client === clientVal)
                && (siteVal   === 'all' || site   === siteVal)
                && (userVal   === 'all' || user   === userVal)
                && (osVal     === 'all' || os     === osVal)
                && (avVal     === 'all' || av     === avVal);

      row.style.display = show ? '' : 'none';
      if (show) visible++;
    });

    updateDropdownsFromVisible();

    const subtitle = `Client: ${clientVal === 'all' ? 'All' : clientVal} | Site: ${siteVal === 'all' ? 'All' : siteVal}`;
    $('#report-subtitle').textContent = subtitle;
    $('#filteredRowCount').textContent = String(visible);

    if (currentSort.colIndex !== null) sortTable(currentSort.colIndex, currentSort.dirAsc, false);
  }

  function sortTable(colIndex, dirAsc = true, toggle = true) {
    const rows = Array.from(tbody.rows);
    const visibleRows = rows.filter(r => r.style.display !== 'none');

    if (toggle) {
      if (currentSort.colIndex === colIndex) currentSort.dirAsc = !currentSort.dirAsc;
      else { currentSort.colIndex = colIndex; currentSort.dirAsc = true; }
    } else {
      currentSort.colIndex = colIndex; currentSort.dirAsc = dirAsc;
    }

    setSortIndicator(currentSort.colIndex, currentSort.dirAsc);

    visibleRows.sort((a, b) => {
      let cmp = 0;
      if (colIndex === 2) {
        const nameA = a.querySelector('.device-name')?.textContent.trim() || a.cells[2].textContent.trim();
        const nameB = b.querySelector('.device-name')?.textContent.trim() || b.cells[2].textContent.trim();
        cmp = nameA.localeCompare(nameB, undefined, { numeric: true, sensitivity: 'base' });
        if (cmp === 0) {
          const sa = a.querySelector('.last-seen')?.getAttribute('data-sort-value') || 'N/A';
          const sb = b.querySelector('.last-seen')?.getAttribute('data-sort-value') || 'N/A';
          const da = new Date(sa); const db = new Date(sb);
          if (!isNaN(da) && !isNaN(db)) cmp = da.getTime() - db.getTime();
          else if (isNaN(da) && !isNaN(db)) cmp = 1;
          else if (!isNaN(da) && isNaN(db)) cmp = -1;
        }
      } else {
        const cellA = a.cells[colIndex], cellB = b.cells[colIndex];
        const vA = cellA.getAttribute('data-sort-value') || cellA.textContent.trim();
        const vB = cellB.getAttribute('data-sort-value') || cellB.textContent.trim();
        if (!isNaN(vA) && !isNaN(vB) && vA !== '' && vB !== '') cmp = parseFloat(vA) - parseFloat(vB);
        else cmp = String(vA).localeCompare(String(vB), undefined, { numeric: true, sensitivity: 'base' });
      }
      return currentSort.dirAsc ? cmp : -cmp;
    });

    tbody.innerHTML = '';
    visibleRows.forEach(r => tbody.appendChild(r));
    rows.filter(r => r.style.display === 'none').forEach(r => tbody.appendChild(r));
  }

  // Header click-to-sort; ignore clicks on the <select>
  $$('#reportTable thead th').forEach((th, idx) => {
    th.addEventListener('click', (e) => {
      if (e.target && e.target.tagName && e.target.tagName.toLowerCase() === 'select') return;
      sortTable(idx, true, true);
    });
  });

  // Wire header selects
  [selects.client, selects.site, selects.user, selects.os, selects.av]
    .filter(Boolean)
    .forEach(sel => sel.addEventListener('change', filterTable));

  function init() {
    updateDropdownsFromVisible();
    filterTable();
    // Initial grouping by Client then Site
    sortTable(0, true, false);
    sortTable(1, true, true);
  }
  document.addEventListener('DOMContentLoaded', init);
})();
</script>
{% endblock %}
